import React, { Component } from 'react';
import Appbar from './Appbar';
import './Game.css'
import { PacmanLoader } from 'react-spinners'
import { Card } from '@material-ui/core'
import { withStyles } from '@material-ui/core';
import PropTypes from 'prop-types';
import Gamearea from './Gamearea';

const style = (theme) => ({
    root: {
      background: 'rgba(255, 255, 255, 0.4)',
      '-webkit-box-shadow' : '5px 5px 15px rgba(0,0,0,0.5)'
    },
    menuButton: {
      marginRight: theme.spacing(2),
    },
    title: {
      flexGrow: 1,
      color: 'black'
    },
  });

class Game extends Component {
    constructor() {
        super();
        this.state = {
            loading: true,
            opnum:[],
            playercards:[]
        }

    }



    render() {
        const {classes}=this.props
        return (
            <div>
                <Appbar auth='true' logout={this.props.logout} />
                <Card className='gameSpace' classes={{root:classes.root}} >
                    <PacmanLoader color='yellow' css='margin-top:20%;margin-left:45%;' loading={this.state.loading}></PacmanLoader>
                    <Gamearea playercards={this.state.playercards} opnum={this.state.opnum}></Gamearea>
                </Card>
            </div>
        )
    }
}
Game.propTypes = {
    classes: PropTypes.object.isRequired,
  };
  export default withStyles(style)(Game);
