import React, { Component } from 'react';
import './Game.css'

export default class Gamearea extends Component {
    constructor() {
        super();
        this.state = {
            playercards:[{color:'red',num:2}],//this.props.cards,
            opnum:''//this.props.ponum
        }
    }  

    handlePlay=(e)=>{
        // var card=e.target;
    }

    render() {
        return (
            <div className='gamearea'>
                <div className='gridItem'>
                    <div className='op1'></div>
                </div>
                <div className='gridItem' id='mid'>
                    <div className='op2'></div>
                    <div className='stack'></div>
                    <div className='op3'></div>
                </div>
                <div className='gridItem' id='playerplace'>
                    {this.state.playercards.map(data=>{
                        return (<img src={require("./cards/"+data.color+"-"+data.num+".png")} alt={"cards/"+data.color+"-"+data.num+".png"} onClick={this.handlePlay}></img>)
                    })}
                </div>
            </div>
        )
    }
}
