import React, { Component } from 'react';
import './Game.css'

export default class Gamearea extends Component {
    constructor() {
        super();
        this.state = {
            cards:[]
        }
    }  

    handlePlay=(e)=>{
        // var card=e.target;
    }

    render() {
        return (
            <div className='gamearea'>
                <div className='gridItem'>
                    <div className='op1'></div>
                </div>
                <div className='gridItem' id='mid'>
                    <div className='op2'></div>
                    <div className='stack'></div>
                    <div className='op3'></div>
                </div>
                <div className='gridItem' id='playerplace'>
                    {this.props.playercards.map(data=>{
                        return (<div style={'background:'+data.color+';'} onClick={this.handlePlay}>{data.num}</div>)
                    })}
                </div>
            </div>
        )
    }
}
